import React, { Component } from 'react'

import './App.css';
import Notifyme from './componets/Notifyme';
import Textbody from './componets/Textbody';
import Countdown from './componets/Countdown';

 class App extends Component {
   constructor(props){
    super(props);

    this.state={
      count : new Date ('jan 1, 2021,00:00:00').getTime,
      now:new Date().getTime,
      deadline:0,
      days: 0,
      hours:0,
      minutes:0,
      seconds:0
    };

  }



  componentDidMount() {
    this.timerID = setInterval(
      () => this.reload(), 1000
    );
  }

  reload = ()=>{
    this.setState(state=>({
      deadline: state.count- state.now,
      days: Math.floor(this.deadline/(1000*60*60*24))
  }));
  }

  render() {


    return (

       <div className="container">
         <Textbody />
         <Notifyme />
         <Countdown />
       </div>


    )
  }
}

export default App;
